// Ensure firebase is available globally
const firebase = window.firebase;
const db = firebase.firestore();
let currentUser = null;
const SYSTEM_PROMPT = `
You are an elite alibi-creator.
Return a valid JSON with exactly these keys:
- "excuse" (string, max 50 words)
- "believability_score" (number between 0 and 1)
- "chat_log" (array of 2–4 objects with keys: "sender" (string), "message" (string))

Each chat log object must look like:
{ "sender": "You", "message": "your message here" }

Do not include explanations or markdown. Output raw JSON only.`;

function getLocalHistory() {
  return JSON.parse(localStorage.getItem("excuseHistory") || "[]");
}

function saveToLocalHistory(entry) {
  const history = getLocalHistory();
  if (!history.find(e => e.id === entry.id)) {
    history.push(entry);
    localStorage.setItem("excuseHistory", JSON.stringify(history));
  }
}

window.signInWithGoogle = function () {
  const provider = new firebase.auth.GoogleAuthProvider();
  firebase.auth().signInWithPopup(provider)
    .then((result) => {
      const user = result.user;
    currentUser = user;
      document.getElementById("userInfo").innerText = `Hello, ${user.displayName}!`;

      const savedKey = localStorage.getItem(user.uid + "-apiKey");
      if (savedKey) document.getElementById("apiKey").value = savedKey;

      document.getElementById("apiKey").addEventListener("input", (e) => {
        localStorage.setItem(user.uid + "-apiKey", e.target.value);
      });
      renderTopExcuses();
    })
    .catch((error) => {
      alert("Login failed: " + error.message);
    });
};

async function generateExcuse() {
  const apiKey = document.getElementById("apiKey").value;
  const scenario = document.getElementById("scenario").value;
  const urgency = document.getElementById("urgency").value;
  const mode = document.getElementById("mode").value;
  const language = document.getElementById("language").value;
  const voice = document.getElementById("voice").checked;
  const output = document.getElementById("output");

  output.innerHTML = "⏳ Asking Gemini (via OpenRouter)...";

  const tone = mode === "apology"
    ? "Respond in a guilt‑tripping, heartfelt apology tone."
    : "Respond in a neutral, believable tone.";

  const langClause = (language === "en" || language === "english")
    ? ""
    : `Respond in ${language} language.`;

  const userPrompt = `
${SYSTEM_PROMPT}
Respond only with raw JSON — no Markdown, no explanation.

Scenario: ${scenario}
Urgency: ${urgency}
Tone: ${tone}
${langClause}
`.trim();

  try {
    const response = await fetch("https://openrouter.ai/api/v1/chat/completions", {
      method: "POST",
      headers: {
        "Authorization": `Bearer ${apiKey}`,
        "Content-Type": "application/json",
        "HTTP-Referer": "http://localhost",
        "X-Title": "Excuse Generator"
      },
      body: JSON.stringify({
        model: "google/gemini-2.5-flash-lite-preview-06-17",
        messages: [
          { role: "system", content: SYSTEM_PROMPT },
          { role: "user", content: userPrompt }
        ]
      })
    });

    const data = await response.json();
    const text = data.choices?.[0]?.message?.content || "";

    const match = text.match(/{[\s\S]+}/);
    if (!match) {
      output.innerText = "❌ Gemini didn't return JSON:\n\n" + text;
      return;
    }

    const parsed = JSON.parse(match[0]);
    const id = btoa(parsed.excuse).slice(0, 12);
    const entry = { id, ts: Date.now(), ...parsed };
    await saveToUserHistory(entry);



// Save to Firestore instead of localStorage
async function saveToUserHistory(entry) {
  if (!currentUser) return;

  try {
    const docRef = db.collection("users").doc(currentUser.uid).collection("topExcuses").doc(entry.id);
    await docRef.set({
      excuse: entry.excuse,
      score: entry.believability_score,
      timestamp: firebase.firestore.FieldValue.serverTimestamp()
    });
    console.log("🔥 Excuse saved to Firestore:", entry.excuse);
  } catch (err) {
    console.error("❌ Failed to save to Firestore:", err);
  }
}



    const chatLogHtml = Array.isArray(parsed.chat_log)
      ? parsed.chat_log
          .filter(entry => entry && entry.sender && entry.message)
          .map(entry => {
            const isUser = entry.sender.toLowerCase() === "me" || entry.sender.toLowerCase() === "you";
            return `
              <div class="chat-bubble ${isUser ? 'me' : 'other'}">
                <strong>${entry.sender}:</strong> ${entry.message}
              </div>`;
          }).join("")
      : "<div class='chat-bubble other'>⚠️ Invalid chat log format</div>";

    output.innerHTML = `
      <div class="excuse-text"><strong>Excuse:</strong> ${parsed.excuse}</div>
      <div class="score"><strong>Believability:</strong> ${parsed.believability_score}</div>
      <div class="chat-log">${chatLogHtml}</div>
    `;

    if (voice) {
