      speechSynthesis.speak(utterance);
    }

  } catch (err) {
    output.innerText = "❌ Error generating excuse:\n" + err.message;
  }
}

function renderTopExcuses() {
  const list = document.getElementById("topExcuses");
  list.innerHTML = "";

  const user = firebase.auth().currentUser;
  if (!user) {
    list.innerHTML = "<li class='list-group-item'>Login to see your top excuses.</li>";
    return;
  }
  firebase.firestore()
    .collection("users")
    .doc(user.uid)
    .collection("topExcuses")
    .orderBy("score", "desc")
    .limit(5)
    .get()
    .then(snapshot => {
      if (snapshot.empty) {
        list.innerHTML = "<li class='list-group-item'>No excuses saved yet.</li>";
        return;
      }

      snapshot.forEach(doc => {
        const data = doc.data();
        const li = document.createElement("li");
        li.classList.add("list-group-item");
        li.textContent = `(${data.score}) ${data.excuse}`;
        list.appendChild(li);
      });
    })
    .catch(err => {
      console.error("Error loading top excuses:", err);
      list.innerHTML = "<li class='list-group-item text-danger'>Error loading excuses.</li>";
    });
}

function triggerEmergency() {
  const number = document.getElementById("emergencyNumber").value;
  const message = document.getElementById("emergencyMessage").value;
  const status = document.getElementById("emergencyStatus");

  const entry = {
    id: "emergency-" + Date.now(),
    ts: Date.now(),
    excuse: "EMERGENCY TRIGGER",
    believability_score: 1.0,
    chat_log: `Sent '${message}' to ${number}`
  };

  saveToLocalHistory(entry);
  renderTopExcuses();
  status.innerText = "✅ Emergency logged locally!";
}
document.addEventListener("DOMContentLoaded", () => {
  renderTopExcuses();
});
function copyExcuse() {
  const text = document.querySelector(".excuse-text")?.innerText?.replace("Excuse:", "").trim();
  if (text) {
    navigator.clipboard.writeText(text);
    alert("Excuse copied to clipboard!");
  } else {
    alert("No excuse to copy!");
  }
}
